package day3_loops;

public class Practice_ASCII_FROM_A2Z {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		// Print ASCHII code from a to z
		
				char ch='A';
				 int a1=(int) ch;
				 
				 while(ch<='Z'){
					 
				System.out.println(ch+":"+a1);
				ch++;
				a1++;
				
				 }
				 
				
				
	}

}
