package ifElse_Programs;

public class Elseif_Ladder {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int age=18;
		
		if (age!=18) {
			System.out.println("congrats on your first vote");
			
			
		}
		else if (age==18) {
			
			System.out.println("you can vote1");
		}
		
		else if (age ==18) {
			System.out.println("you can not vote2");
			
		}
		
		
	}

}
